mapper.select:
SELECT COUNT(*) FROM books
WHERE title = #{title}
AND publication_year = #{publicationYear}

SELECT b.id, b.title, b.slug, b.edition, b.cover_image_url, b.publisher, b.publication_year, b.file_url, b.file_format, b.file_size, b.total_pages, b.description, b.summary, b.estimated_read_time, l.name as language, b.total_word, b.difficulty_level, b.view_count, b.read_count, b.download_count, b.subtitle, b.series_id, b.series_order, cs.name as copyright_status, b.published_at, b.category, a.id as author_id, a.name as author_name, a.slug as author_slug, a.birth_date as author_birth_date, a.death_date as author_death_date, a.birth_place as author_birth_place, a.nationality as author_nationality, a.photo_url as author_photo_url, c.id as contributor_id, c.name as contributor_name, c.slug as contributor_slug, bc.role as contributor_role, c.website_url as contributor_website_url, g.id as genre_id, g.name as genre_name, g.slug as genre_slug, g.color_hex as genre_color_hex, g.is_fiction as genre_is_fiction, COALESCE(rs.total_ratings, 0) as total_ratings, COALESCE(rs.total_angry, 0) as total_angry, COALESCE(rs.total_likes, 0) as total_likes, COALESCE(rs.total_loves, 0) as total_loves, COALESCE(rs.total_dislikes, 0) as total_dislikes, COALESCE(rs.total_sad, 0) as total_sad, COALESCE(rs.total_comments, 0) as total_comments, COALESCE(rs.average_rating, 0.0) as average_rating FROM books b JOIN languages l ON b.language_id = l.id JOIN copyright_statuses cs ON b.copyright_status_id = cs.id LEFT JOIN book_authors ba ON b.id = ba.book_id LEFT JOIN authors a ON ba.author_id = a.id LEFT JOIN book_contributors bc ON b.id = bc.book_id LEFT JOIN contributors c ON bc.contributor_id = c.id LEFT JOIN book_genres bg ON b.id = bg.book_id LEFT JOIN genres g ON bg.genre_id = g.id LEFT JOIN ( SELECT book_id, COUNT(CASE WHEN reaction_type = 'RATING' THEN 1 END) as total_ratings, COUNT(CASE WHEN reaction_type = 'ANGRY' THEN 1 END) as total_angry, COUNT(CASE WHEN reaction_type = 'LIKE' THEN 1 END) as total_likes, COUNT(CASE WHEN reaction_type = 'LOVE' THEN 1 END) as total_loves, COUNT(CASE WHEN reaction_type = 'DISLIKE' THEN 1 END) as total_dislikes, COUNT(CASE WHEN reaction_type = 'SAD' THEN 1 END) as total_sad, COUNT(CASE WHEN reaction_type = 'COMMENT' THEN 1 END) as total_comments, AVG(CASE WHEN reaction_type = 'RATING' THEN rating END) as average_rating FROM reactions GROUP BY book_id ) rs ON b.id = rs.book_id
AND LOWER(b.title) LIKE CONCAT('%', LOWER(#{searchTitle}), '%')
AND b.series_id = #{seriesId}
AND EXISTS ( SELECT 1 FROM book_genres bg2 WHERE bg2.book_id = b.id AND bg2.genre_id = #{genreId} )
AND EXISTS ( SELECT 1 FROM book_genres bg3 JOIN genres g3 ON bg3.genre_id = g3.id WHERE bg3.book_id = b.id AND g3.parent_genre_id = #{subGenreId} )
ORDER BY b.id,
a.name ${sortType}
b.published_at ${sortType}
b.estimated_read_time ${sortType}
b.total_word ${sortType}
rs.average_rating ${sortType}
b.view_count ${sortType}
b.title ${sortType}
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE books
SET read_count = read_count + 1
WHERE id = #{bookId}

UPDATE books SET
title = #{title}, slug = #{slug}, subtitle = #{subtitle}, series_id = #{seriesId}, series_order = #{seriesOrder}, isbn = #{isbn}, publication_year = #{publicationYear}, publisher = #{publisher}, language_id = #{languageId}, description = #{description}, summary = #{summary}, cover_image_url = #{coverImageUrl}, cover_image_path = #{coverImagePath}, file_url = #{fileUrl}, file_path = #{filePath}, file_format = #{fileFormat}, file_size = #{fileSize}, total_word = #{totalWord}, total_pages = #{totalPages}, estimated_read_time = #{estimatedReadTime}, difficulty_level = #{difficultyLevel}, copyright_status_id = #{copyrightStatusId}, view_count = #{viewCount}, read_count = #{readCount}, download_count = #{downloadCount}, average_rating = #{averageRating}, total_reviews = #{totalReviews}, is_featured = #{isFeatured}, is_active = #{isActive}, published_at = #{publishedAt}, category = #{category}, updated_at = CURRENT_TIMESTAMP,
WHERE id = #{id}

UPDATE books SET
title = #{title}, slug = #{slug}, subtitle = #{subtitle}, series_id = #{seriesId}, series_order = #{seriesOrder}, isbn = #{isbn}, publication_year = #{publicationYear}, publisher = #{publisher}, language_id = #{languageId}, description = #{description}, summary = #{summary}, cover_image_url = #{coverImageUrl}, cover_image_path = #{coverImagePath}, file_url = #{fileUrl}, file_path = #{filePath}, file_format = #{fileFormat}, file_size = #{fileSize}, total_word = #{totalWord}, total_pages = #{totalPages}, estimated_read_time = #{estimatedReadTime}, difficulty_level = #{difficultyLevel}, copyright_status_id = #{copyrightStatusId}, view_count = #{viewCount}, read_count = #{readCount}, download_count = #{downloadCount}, average_rating = #{averageRating}, total_reviews = #{totalReviews}, is_featured = #{isFeatured}, is_active = #{isActive}, published_at = #{publishedAt}, category = #{category}, updated_at = CURRENT_TIMESTAMP,
WHERE id = #{id}
