mapper.select:
SELECT DISTINCT b.id, b.title, b.slug, b.edition, b.cover_image_url, b.publisher, b.publication_year, b.file_url, b.file_format, b.file_size, b.total_pages, b.description, b.estimated_read_time, l.name as language, b.total_word, b.difficulty_level, b.view_count, b.read_count, b.download_count, b.subtitle, b.series_id, b.series_order, cs.name as copyright_status, b.published_at, b.category, (SELECT STRING_AGG(a.name, ', ') FROM book_authors ba JOIN authors a ON ba.author_id = a.id WHERE ba.book_id = b.id) as author_names, (SELECT STRING_AGG(a.slug, ', ') FROM book_authors ba JOIN authors a ON ba.author_id = a.id WHERE ba.book_id = b.id) as author_slugs, (SELECT STRING_AGG(c.name || ' (' || bc.role || ')', ', ') FROM book_contributors bc JOIN contributors c ON bc.contributor_id = c.id WHERE bc.book_id = b.id) as contributors, (SELECT STRING_AGG(g.name, ', ') FROM book_genres bg JOIN genres g ON bg.genre_id = g.id WHERE bg.book_id = b.id) as genres, COALESCE(rs.total_ratings, 0) as total_ratings, COALESCE(rs.total_angry, 0) as total_angry, COALESCE(rs.total_likes, 0) as total_likes, COALESCE(rs.total_loves, 0) as total_loves, COALESCE(rs.total_dislikes, 0) as total_dislikes, COALESCE(rs.total_sad, 0) as total_sad, COALESCE(rs.total_comments, 0) as total_comments, COALESCE(rs.average_rating, 0.0) as average_rating FROM books b JOIN languages l ON b.language_id = l.id JOIN copyright_statuses cs ON b.copyright_status_id = cs.id LEFT JOIN ( SELECT book_id, COUNT(CASE WHEN UPPER(reaction_type) = 'RATING' THEN 1 END) as total_ratings, COUNT(CASE WHEN UPPER(reaction_type) = 'ANGRY' THEN 1 END) as total_angry, COUNT(CASE WHEN UPPER(reaction_type) = 'LIKE' THEN 1 END) as total_likes, COUNT(CASE WHEN UPPER(reaction_type) = 'LOVE' THEN 1 END) as total_loves, COUNT(CASE WHEN UPPER(reaction_type) = 'DISLIKE' THEN 1 END) as total_dislikes, COUNT(CASE WHEN UPPER(reaction_type) = 'SAD' THEN 1 END) as total_sad, COUNT(CASE WHEN comment IS NOT NULL THEN 1 END) as total_comments, AVG(CASE WHEN rating IS NOT NULL THEN rating END) as average_rating FROM reactions GROUP BY book_id ) rs ON b.id = rs.book_id
AND LOWER(b.title) LIKE CONCAT('%', LOWER(#{searchTitle}), '%')
AND b.series_id = #{seriesId}
AND EXISTS ( SELECT 1 FROM book_genres bg2 WHERE bg2.book_id = b.id AND bg2.genre_id = #{genreId} )
AND EXISTS ( SELECT 1 FROM book_genres bg3 JOIN genres g3 ON bg3.genre_id = g3.id WHERE bg3.book_id = b.id AND g3.parent_genre_id = #{subGenreId} )
ORDER BY b.id,
b.published_at ${sortType}
b.estimated_read_time ${sortType}
b.total_word ${sortType}
rs.average_rating ${sortType}
b.view_count ${sortType}
b.title ${sortType}
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE books
SET read_count = read_count + 1
WHERE id = #{bookId}

UPDATE books SET
title = #{title}, slug = #{slug}, subtitle = #{subtitle}, series_id = #{seriesId}, series_order = #{seriesOrder}, edition = #{edition}, publication_year = #{publicationYear}, publisher = #{publisher}, language_id = #{languageId}, description = #{description}, cover_image_url = #{coverImageUrl}, cover_image_path = #{coverImagePath}, file_url = #{fileUrl}, file_path = #{filePath}, file_format = #{fileFormat}, file_size = #{fileSize}, total_word = #{totalWord}, total_pages = #{totalPages}, estimated_read_time = #{estimatedReadTime}, difficulty_level = #{difficultyLevel}, copyright_status_id = #{copyrightStatusId}, view_count = #{viewCount}, read_count = #{readCount}, download_count = #{downloadCount}, is_featured = #{isFeatured}, is_active = #{isActive}, published_at = #{publishedAt}, category = #{category}, updated_at = CURRENT_TIMESTAMP,
WHERE id = #{id}

UPDATE books SET
title = #{title}, slug = #{slug}, subtitle = #{subtitle}, series_id = #{seriesId}, series_order = #{seriesOrder}, edition = #{edition}, publication_year = #{publicationYear}, publisher = #{publisher}, language_id = #{languageId}, description = #{description}, cover_image_url = #{coverImageUrl}, cover_image_path = #{coverImagePath}, file_url = #{fileUrl}, file_path = #{filePath}, file_format = #{fileFormat}, file_size = #{fileSize}, total_word = #{totalWord}, total_pages = #{totalPages}, estimated_read_time = #{estimatedReadTime}, difficulty_level = #{difficultyLevel}, copyright_status_id = #{copyrightStatusId}, view_count = #{viewCount}, read_count = #{readCount}, download_count = #{downloadCount}, is_featured = #{isFeatured}, is_active = #{isActive}, published_at = #{publishedAt}, category = #{category}, updated_at = CURRENT_TIMESTAMP,
WHERE id = #{id}
