mapper.select:
SELECT COUNT(*) FROM books
WHERE title = #{title}
AND publication_year = #{publicationYear}

SELECT DISTINCT ON (b.id) b.id as book_id, b.title, b.slug, b.isbn, b.cover_image_url, b.publisher, b.publication_year, b.file_url, b.file_format, b.file_size, b.total_pages, b.description, b.summary, b.estimated_read_time, b.language_id, b.total_word, b.difficulty_level, b.view_count, b.read_count, b.download_count, b.average_rating, b.total_reviews, b.subtitle, b.series_id, b.series_order, b.copyright_status_id, b.published_at, b.category, a.id as author_id, a.name as author_name, a.slug as author_slug, a.birth_date, a.death_date, a.birth_place, a.nationality, a.photo_url, g.id as genre_id, g.name as genre_name, g.slug as genre_slug, g.color_hex, g.is_fiction, t.id as tag_id, t.name as tag_name, t.slug as tag_slug FROM books b LEFT JOIN book_authors ba ON b.id = ba.book_id LEFT JOIN authors a ON ba.author_id = a.id LEFT JOIN book_genres bg ON b.id = bg.book_id LEFT JOIN genres g ON bg.genre_id = g.id LEFT JOIN book_tags bt ON b.id = bt.book_id LEFT JOIN tags t ON bt.tag_id = t.id
AND LOWER(b.title) LIKE CONCAT('%', LOWER(#{searchTitle}), '%')
AND b.series_id = #{seriesId}
AND EXISTS ( SELECT 1 FROM book_genres bg2 WHERE bg2.book_id = b.id AND bg2.genre_id = #{genreId} )
AND EXISTS ( SELECT 1 FROM book_genres bg3 JOIN genres g3 ON bg3.genre_id = g3.id WHERE bg3.book_id = b.id AND g3.parent_genre_id = #{subGenreId} )
ORDER BY b.id,
a.name ${sortType}
b.published_at ${sortType}
b.estimated_read_time ${sortType}
b.total_word ${sortType}
b.average_rating ${sortType}
b.view_count ${sortType}
b.title ${sortType}
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE books
SET read_count = read_count + 1
WHERE id = #{bookId}

UPDATE books SET
title = #{title}, slug = #{slug}, subtitle = #{subtitle}, series_id = #{seriesId}, series_order = #{seriesOrder}, isbn = #{isbn}, publication_year = #{publicationYear}, publisher = #{publisher}, language_id = #{languageId}, description = #{description}, summary = #{summary}, cover_image_url = #{coverImageUrl}, cover_image_path = #{coverImagePath}, file_url = #{fileUrl}, file_path = #{filePath}, file_format = #{fileFormat}, file_size = #{fileSize}, total_word = #{totalWord}, total_pages = #{totalPages}, estimated_read_time = #{estimatedReadTime}, difficulty_level = #{difficultyLevel}, copyright_status_id = #{copyrightStatusId}, view_count = #{viewCount}, read_count = #{readCount}, download_count = #{downloadCount}, average_rating = #{averageRating}, total_reviews = #{totalReviews}, is_featured = #{isFeatured}, is_active = #{isActive}, published_at = #{publishedAt}, category = #{category}, updated_at = CURRENT_TIMESTAMP,
WHERE id = #{id}

UPDATE books SET
title = #{title}, slug = #{slug}, subtitle = #{subtitle}, series_id = #{seriesId}, series_order = #{seriesOrder}, isbn = #{isbn}, publication_year = #{publicationYear}, publisher = #{publisher}, language_id = #{languageId}, description = #{description}, summary = #{summary}, cover_image_url = #{coverImageUrl}, cover_image_path = #{coverImagePath}, file_url = #{fileUrl}, file_path = #{filePath}, file_format = #{fileFormat}, file_size = #{fileSize}, total_word = #{totalWord}, total_pages = #{totalPages}, estimated_read_time = #{estimatedReadTime}, difficulty_level = #{difficultyLevel}, copyright_status_id = #{copyrightStatusId}, view_count = #{viewCount}, read_count = #{readCount}, download_count = #{downloadCount}, average_rating = #{averageRating}, total_reviews = #{totalReviews}, is_featured = #{isFeatured}, is_active = #{isActive}, published_at = #{publishedAt}, category = #{category}, updated_at = CURRENT_TIMESTAMP,
