mapper.update:
UPDATE book_ratings
SET rating = #{rating},
updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT id, user_id, book_id, rating, created_at, updated_at
FROM book_ratings
WHERE id = #{id}

SELECT id, user_id, book_id, rating, created_at, updated_at
FROM book_ratings
WHERE user_id = #{userId} AND book_id = #{bookId}
