mapper.update:
UPDATE book_review_feedbacks
SET is_helpful = #{isHelpful},
updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT id, user_id, review_id, is_helpful, created_at, updated_at
FROM book_review_feedbacks
WHERE id = #{id}

